import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link, useParams, useNavigate } from 'react-router-dom'
import {
  Row,
  Col,
  Image,
  ListGroup,
  Card,
  Button,
  Form,
} from 'react-bootstrap'
import { listStaffDetails } from '../actions/staffAction'
import Rating from '../components/Rating'
import Loader from '../components/Loader'
import Message from '../components/Message'

const StaffScreen = () => {
  // State for available times and selected date/slot
  const [availableTimes, setAvailableTimes] = useState([])
  const [selectedDate, setSelectedDate] = useState('')
  const [selectedSlot, setSelectedSlot] = useState('')

  const dispatch = useDispatch()
  const { id } = useParams()
  const staffDetails = useSelector((state) => state.staffDetails)
  const { loading, error, staff } = staffDetails
  // Handle fetching staff details
  useEffect(() => {
    dispatch(listStaffDetails(id))
  }, [dispatch, id])

  // Update available times when staff details are fetched
  useEffect(() => {
    if (staff && staff.availableTimes) {
      setAvailableTimes(staff.availableTimes.map((time) => ({
        date: time.date.slice(0, 10), // Use just the date part for comparison
        slots: time.slots,
      })))
    }
  }, [staff])


  // Handle date change
  const handleDateChange = (e) => {
    const newDate = e.target.value
    setSelectedDate(newDate)
    // Reset selected slot
    setSelectedSlot('')
  }

  // Filter slots for the selected date
  const availableSlots = availableTimes.find(time => time.date === selectedDate)?.slots || []

  //添加预约
  const addToAppointmentHandler = () => { }

  return (
    <>
      <Link className='btn btn-dark my-3' to='/'>
        返回主页
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Row>
          <Col md={6}>
            <Image src={staff.image} alt={staff.name} fluid />
          </Col>
          <Col md={3}>
            <ListGroup variant='flush'>
              <ListGroup.Item>
                <h3>{staff.name}</h3>
              </ListGroup.Item>
              <ListGroup.Item>
                {staff.rating && (
                  <Rating
                    value={staff.rating}
                    text={`${staff.numReviews}条评论`}
                  />
                )}
              </ListGroup.Item>
              <ListGroup.Item>Position:{staff.position}</ListGroup.Item>
              <ListGroup.Item>Department:{staff.department}</ListGroup.Item>
              <ListGroup.Item>Description:{staff.description}</ListGroup.Item>
            </ListGroup>
          </Col>
          <Col md={3}>
            <Card>
              <ListGroup variant='flush'>
                <ListGroup.Item>
                  <Row>
                    <Col>Position：</Col>
                    <Col>
                      <strong>{staff.position}</strong>
                    </Col>
                  </Row>
                </ListGroup.Item>

                <Form>
                  {/* Date selection */}
                  <Form.Group controlId="dateSelect">
                    <Form.Label>Date</Form.Label>
                    <Form.Control as="select" value={selectedDate} onChange={handleDateChange}>
                      {availableTimes.map((time, index) => (
                        <option key={index} value={time.date}>
                          {time.date}
                        </option>
                      ))}
                    </Form.Control>
                  </Form.Group>

                  {/* Slot selection */}
                  <Form.Group controlId="slotSelect" disabled={!selectedDate}>
                    <Form.Label>Slot</Form.Label>
                    <Form.Control as="select" value={selectedSlot} onChange={(e) => setSelectedSlot(e.target.value)}>
                      {availableSlots.map((slot, index) => (
                        <option key={index} value={slot}>
                          {slot}
                        </option>
                      ))}
                    </Form.Control>
                  </Form.Group>

                  {/* Add to appointment button */}
                  <Button onClick={addToAppointmentHandler} disabled={!selectedSlot}>
                    Add to Appointment
                  </Button>
                </Form>

              </ListGroup>
            </Card>
          </Col>
        </Row>
      )}
    </>
  )
}

export default StaffScreen
