// components/OrdersScreen.js
import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { fetchOrdersByUserId } from '../actions/orderActions'

const OrdersScreen = ({ userId }) => {
  const dispatch = useDispatch()
  const { loading, orders, error } = useSelector(state => state.userOrder) // 使用 userOrder

  useEffect(() => {
    dispatch(fetchOrdersByUserId(userId))
  }, [dispatch, userId])

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          <h2>Your Orders:</h2>
          <ul>
            {orders.map(order => (
              <li key={order._id}>{order.orderItems.map(item => <div>{item.name}</div>)}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )
}

export default OrdersScreen
