import React, { useState, useEffect } from 'react'
import axios from 'axios'

const DataFetchComponent = () => {
  const [totalResults, setTotalResults] = useState(0)

  useEffect(() => {
    // 定义我们的查询参数
    const params = {
      resource_id: 'DISP_NAMEADDRESS_202403', // the resource id
      limit: 5, // get 5 results
      q: '{"TOTAL_QUANTITY":"40:*"}' // query for 'TOTAL_QUANTITY contains 40'
    }

    // 使用axios发送GET请求
    axios.get('https://opendata.nhsbsa.net/api/3/action/datastore_search', { params })
      .then(response => {
        // 成功获取响应后，我们更新状态以存储总结果数
        setTotalResults(response.data.result.total)
      })
      .catch(error => {
        // 如果请求失败，我们可以在这里处理错误
        console.error('There was an error!', error)
      })
  }, []) // 空依赖数组意味着这个effect只会在组件挂载后运行一次

  // 现在我们可以在UI中显示总结果数
  return (
    <div>
      <h1>DataFetchComponent</h1>
      <p>Total results found: {totalResults}</p>
    </div>
  )
}

export default DataFetchComponent
