import React, { useEffect } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, Card, ListGroup, Button } from 'react-bootstrap'
import { editOrderById } from '../actions/staffAction'
import Loader from '../components/Loader'
import Message from '../components/Message'

const EditOrderScreen = () => {
  const { id: orderId } = useParams()
  const navigate = useNavigate()
  const dispatch = useDispatch()

  const editOrder = useSelector(state => state.editOrder) // 确保这个名称与你在 rootReducer 中设置的名称匹配
  const { loading, error, orderDetial } = editOrder // 注意这里是 orderDetial

  useEffect(() => {
    if (orderId) {
      dispatch(editOrderById(orderId))
    }
  }, [dispatch, orderId])



  return (
    <>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <>
          <Col>
            <Row>
              {orderDetial.user.name}
            </Row>
            <Row>
              {orderDetial.user.email}
            </Row>
          </Col>
          <Col>
            {orderDetial.illnessDescription}
          </Col>
          <Col>
            {orderDetial.orderItems[0].status}
          </Col>
        </>


      )
      }
    </>
  )
}

export default EditOrderScreen
