import React, { useEffect } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, Card, ListGroup, Button } from 'react-bootstrap'
import { editOrderById } from '../actions/staffAction'
import Loader from '../components/Loader'
import Message from '../components/Message'

const EditOrderScreen = () => {
  const { id: orderId } = useParams()
  const navigate = useNavigate()
  const dispatch = useDispatch()

  const editOrder = useSelector(state => state.editOrder) // Ensure this name matches what you've set in your rootReducer
  const { loading, error, orderDetial } = editOrder

  useEffect(() => {
    if (!orderDetial || orderDetial._id !== orderId) {
      dispatch(editOrderById(orderId))
    }
  }, [dispatch, orderId, orderDetial])

  const getStatusMessage = (status) => {
    switch (status) {
      case 'Scheduled':
        return { variant: 'info', message: 'Scheduled' }
      case 'Completed':
        return { variant: 'success', message: 'Completed' }
      case 'Cancelled':
        return { variant: 'danger', message: 'Cancelled' }
      case 'No Show':
        return { variant: 'warning', message: 'No Show' }
      default:
        return { variant: 'secondary', message: 'Unknown' }
    }
  }

  return (
    <div>
      <h1 className="text-center">Edit Order Details</h1>
      {loading ? <Loader /> : error ? <Message variant="danger">{error}</Message> : (
        orderDetial && (
          <Row className="justify-content-center">
            <Col md={8}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h4>Order ID: {orderDetial._id}</h4>
                </ListGroup.Item>
                <ListGroup.Item>
                  <strong>User:</strong> {orderDetial.user.name} ({orderDetial.user.email})
                </ListGroup.Item>
                <ListGroup.Item>
                  <strong>Illness Description:</strong> {orderDetial.illnessDescription}
                </ListGroup.Item>
                <ListGroup.Item>
                  <strong>Status:</strong>
                  <Message {...getStatusMessage(orderDetial.status)}>{getStatusMessage(orderDetial.status).message}</Message>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Button variant="primary" onClick={() => navigate(`/edit/${orderDetail._id}`)}>Edit Order</Button>
                </ListGroup.Item>
              </ListGroup>
            </Col>
          </Row>
        )
      )}
    </div>
  )
}

export default EditOrderScreen
