import React, { useEffect } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, Card, Button } from 'react-bootstrap'
import { editOrderById, updateOrderStatus } from '../actions/staffAction'
import Loader from '../components/Loader'
import Message from '../components/Message'

const EditOrderScreen = () => {
  const { id: orderId } = useParams()
  const navigate = useNavigate()
  const dispatch = useDispatch()

  const editOrder = useSelector(state => state.editOrder)
  const { loading, error, orderDetial } = editOrder
  const updateStatusInfo = useSelector(state => state.orderUpdateStatus)
  const { success } = updateStatusInfo

  useEffect(() => {
    dispatch(editOrderById(orderId))
  }, [dispatch, orderId])

  useEffect(() => {
    // 如果状态更新成功，重新获取订单详情
    if (success) {
      dispatch(editOrderById(orderId))
    }
  }, [dispatch, orderId, success]) // 添加 success 作为依赖项

  const updateStatus = (itemIndex, newStatus) => {
    dispatch(updateOrderStatus(orderId, itemIndex, newStatus))
  }

  const handleReferral = () => {
    navigate(`/`)

  }

  if (loading) return <Loader />
  if (error) return <Message variant='danger'>{error}</Message>

  if (!orderDetial) return <Message variant='info'>Order not found</Message>

  return (
    <Row>
      <Col md={6}>
        <Card>
          <Card.Body>
            <Card.Title>{orderDetial.user.name}</Card.Title>
            <Card.Text>{orderDetial.user.email}</Card.Text>
            <Card.Text>{orderDetial.illnessDescription}</Card.Text>
            <Card.Text>Status: {orderDetial.orderItems[0].status}</Card.Text>
            <Button variant="success" onClick={() => updateStatus(0, 'Completed')}>Completed</Button>
            <Button variant="danger" onClick={() => updateStatus(0, 'Cancelled')}>Cancelled</Button>
            <Button variant="warning" onClick={handleReferral}>Refer</Button>
          </Card.Body>
        </Card>
      </Col>
    </Row>
  )
}

export default EditOrderScreen
