import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link, useParams, useNavigate } from 'react-router-dom'
import {
  Row,
  Col,
  Image,
  ListGroup,
  Card,
  Button,
  Form,
} from 'react-bootstrap'
import { listStaffDetails } from '../actions/staffAction'
import Rating from '../components/Rating'
import Loader from '../components/Loader'
import Message from '../components/Message'

const StaffScreen = ({ match }) => {
  const [availableTimes, setAvailableTimes] = useState([
    {
      date: new Date('2024-05-03T00:00:00.000+00:00'),
      slots: ['10:00']
    },
  ])

  // 用于存储选择的日期和时段的状态
  const [selectedDate, setSelectedDate] = useState('')
  const [selectedSlot, setSelectedSlot] = useState('')

  // 处理日期更改时更新时段的逻辑
  const handleDateChange = (e) => {
    setSelectedDate(e.target.value)
    // 重置选择的时段
    setSelectedSlot('')
  }

  // 获取当前选中日期的可用时段
  const availableSlots = selectedDate
    ? availableTimes.find((time) => time.date === selectedDate)?.slots || []
    : []


  const dispatch = useDispatch()
  const { id } = useParams()
  const staffDetails = useSelector((state) => state.staffDetails)
  const { loading, error, staff } = staffDetails
  useEffect(() => {
    dispatch(listStaffDetails(id))  // 直接使用 id
  }, [dispatch, id])

  let navigate = useNavigate()

  //添加到购物车事件
  const addToAppointmentHandler = () => {

  }

  return (
    <>
      <Link className='btn btn-dark my-3' to='/'>
        返回主页
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Row>
          <Col md={6}>
            <Image src={staff.image} alt={staff.name} fluid />
          </Col>
          <Col md={3}>
            <ListGroup variant='flush'>
              <ListGroup.Item>
                <h3>{staff.name}</h3>
              </ListGroup.Item>
              <ListGroup.Item>
                {staff.rating && (
                  <Rating
                    value={staff.rating}
                    text={`${staff.numReviews}条评论`}
                  />
                )}
              </ListGroup.Item>
              <ListGroup.Item>Position:{staff.position}</ListGroup.Item>
              <ListGroup.Item>Department:{staff.department}</ListGroup.Item>
              <ListGroup.Item>Description:{staff.description}</ListGroup.Item>
            </ListGroup>
          </Col>
          <Col md={3}>
            <Card>
              <ListGroup variant='flush'>
                <ListGroup.Item>
                  <Row>
                    <Col>Position：</Col>
                    <Col>
                      <strong>{staff.position}</strong>
                    </Col>
                  </Row>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Row>
                    <Col>Date:</Col>
                    <Col>
                      <Form.Control as='select' value={selectedDate} onChange={handleDateChange}>
                        <option value="">选择日期</option>
                        {availableTimes.map((time, index) => (
                          <option key={index} value={time.date.toISOString()}>
                            {time.date.toISOString().slice(0, 10)} // 或者使用其他格式化日期的方式
                          </option>
                        ))}
                      </Form.Control>
                    </Col>


                    <Col>Slot</Col>
                    <Col>
                      <Form.Control
                        as='select'
                        value={selectedSlot}
                        onChange={(e) => setSelectedSlot(e.target.value)}
                        disabled={!selectedDate} // 如果没有选择日期，则禁用时段选择
                      >
                        <option value="">选择时段</option>
                        {availableSlots.map((slot, index) => (
                          <option key={index} value={slot}>
                            {slot}
                          </option>
                        ))}
                      </Form.Control>
                    </Col>
                  </Row>
                </ListGroup.Item>

                <ListGroup.Item>
                  <Button
                    onClick={addToAppointmentHandler}
                    className='btn-block'
                    type='button'

                  >
                    添加到预约
                  </Button>
                </ListGroup.Item>

              </ListGroup>
            </Card>
          </Col>
        </Row>
      )}
    </>
  )
}

export default StaffScreen
