import React, { useState, useEffect } from 'react'
import { Link, useNavigate, useLocation } from 'react-router-dom'
import { Form, Button, Row, Col } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { register } from '../actions/userAction'
import Message from '../components/Message'
import Loader from '../components/Loader'
import FormContainer from '../components/FormContainer'

const MedicalScreen = () => {
  const [prescription, setPrescription] = useState('')
  const [result, setResult] = useState('')
  const [test, setTest] = useState('')
  const [date, setDate] = useState('')

  const dispatch = useDispatch()
  const navigate = useNavigate()
  const location = useLocation()
  const editOrder = useSelector(state => state.editOrder)
  const { loading, error, orderDetial } = editOrder



  //表单提交函数
  const submitHandler = (e) => {
    e.preventDefault()

    dispatch()

  }



  return (
    <FormContainer>
      <h1>EDIT MEDICAL HISTORY</h1>
      {/* {message && <Message variant='danger'>{message}</Message>}
      {error && <Message variant='danger'>{error}</Message>}
      {loading && <Loader />} */}
      <Form onSubmit={submitHandler}>

        <Form.Group controlId='prescription'>
          <Form.Label>Medical Prescription:</Form.Label>
          <Form.Control
            type='prescription'
            placeholder='Please enter prescription'
            value={prescription}
            onChange={(e) => setPrescription(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId='result'>
          <Form.Label>Diagnostic Result:</Form.Label>
          <Form.Control
            type='result'
            placeholder='Please enter Result'
            value={result}
            onChange={(e) => setResult(e.target.value)}
          ></Form.Control>
        </Form.Group>


        <Form.Group controlId='test'>
          <Form.Label>Test：</Form.Label>
          <Form.Control
            as='select' // 下拉选择框
            value={test}
            onChange={(e) => setTest(e.target.value)}
          >
            <option value=''>Select </option>
            <option value='Blood test'>Blood test</option>
            <option value='Urine test'>Urine test</option>
            <option value='other'>other</option>
          </Form.Control>
        </Form.Group>


        <Form.Group controlId='date'>
          <Form.Label>Date of Birth：</Form.Label>
          <Form.Control
            type='date' // 注意这里使用了 date 类型
            placeholder='Please select a date'
            value={date}
            onChange={(e) => setDate(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Button type='submit' variant='primary'>
          EDIT MEDICAL HISTORY
        </Button>
      </Form>
    </FormContainer>
  )
}

export default MedicalScreen
