import React, { useEffect } from 'react'
import { Link, useNavigate, useLocation, useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, ListGroup, Image, Form, Button } from 'react-bootstrap'
import { addToCart } from '../actions/cartActions'

const CartScreen = () => { // 移除了 match 和 history，因为我们会使用 useParams 和 useNavigate
  const { id: productId } = useParams() // 使用 useParams 获取路由参数
  const navigate = useNavigate()
  const location = useLocation() // 使用 useLocation 获取查询字符串
  const qty = location.search ? Number(new URLSearchParams(location.search).get('qty')) : 1
  const dispatch = useDispatch()
  const cart = useSelector((state) => state.cart)
  const { cartItems } = cart || { cartItems: [] }

  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty))
    }
  }, [dispatch, productId, qty])
  return <div>Cart</div>
}

export default CartScreen
