import React, { useEffect } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, Card, ListGroup, Button } from 'react-bootstrap'
import { editOrderById, updateOrderStatus } from '../actions/staffAction'
import Loader from '../components/Loader'
import Message from '../components/Message'

const EditOrderScreen = () => {
  const { id: orderId } = useParams()
  const navigate = useNavigate()
  const dispatch = useDispatch()

  const editOrder = useSelector(state => state.editOrder) // 确保这个名称与你在 rootReducer 中设置的名称匹配
  const { loading, error, orderDetial } = editOrder // 注意这里是 orderDetial

  useEffect(() => {
    if (orderId) {
      dispatch(editOrderById(orderId))
    }
  }, [dispatch, orderId])



  const updateStatus = (newStatus) => {
    dispatch(updateOrderStatus(orderId, itemIndex, newStatus))
  }

  const handleReferral = () => {
    // Logic for referral
    console.log('Handle referral')
    // Implement your referral logic here
  }

  return (
    <>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : orderDetial ? (
        <Row>
          <Col md={6}>
            <Card>
              <Card.Body>
                <Card.Title>{orderDetial.user.name}</Card.Title>
                <Card.Text>
                  {orderDetial.user.email}
                </Card.Text>
                <Card.Text>
                  {orderDetial.illnessDescription}
                </Card.Text>
                <Card.Text>
                  Status: {orderDetial.orderItems[0].status}
                </Card.Text>
                <Button variant="success" onClick={() => updateStatus(orderId, 0, 'Completed')}>Completed</Button>
                <Button variant="danger" onClick={() => updateStatus('Cancelled')}>Cancelled</Button>
                <Button variant="warning" onClick={handleReferral}>Refer</Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      ) : (
        <Message variant='info'>Order not found</Message>
      )}
    </>
  )
}

export default EditOrderScreen
