import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { ListGroup, Row, Col, Image, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { useParams } from 'react-router-dom'
import { getOrderDetails } from '../actions/orderAction'
import Message from '../components/Message'
import Loader from '../components/Loader'

const OrderScreen = () => {
  // 使用 useParams 钩子获取路由参数
  const { id: orderId } = useParams()
  const dispatch = useDispatch()

  const orderDetails = useSelector((state) => state.orderDetails)
  const { order, loading, error } = orderDetails


  useEffect(() => {
    // 确保 order 已经定义并且 order._id 不等于 orderId
    if (!order || (order && order._id !== orderId)) {
      dispatch(getOrderDetails(orderId))
    }
    // eslint-disable-next-line
  }, [order, orderId])


  const getStatusMessage = (status) => {
    switch (status) {
      case 'Scheduled':
        return { variant: 'info', message: '预定' }
      case 'Completed':
        return { variant: 'success', message: '已完成' }
      case 'Cancelled':
        return { variant: 'danger', message: '已取消' }
      case 'No Show':
        return { variant: 'warning', message: '未出现' }
      default:
        return { variant: 'secondary', message: '状态未知' }
    }
  }


  return loading ? (
    <Loader />
  ) : error ? (
    <Message variant='danger'>{error}</Message>
  ) : order ? (
    <>
      <h1>Appointment Num：{order._id}</h1>
      <Row>
        <Col md={8}>
          <ListGroup variant='flush'>
            <ListGroup.Item>
              <h2> <p>
                <strong>Name:</strong>
                {order.user.name}
              </p></h2>

              <p>
                {' '}
                <strong>Email:</strong>
                <a href={`mailto:${order.user.email}`}>{order.user.email}</a>
              </p>
              <h2>Description of the illness or complain</h2>
              <h2>  <p>
                {order.illnessDescription}
              </p></h2>


            </ListGroup.Item>



            <ListGroup.Item>
              <h2>Appointment Request Form</h2>
              {order.orderItems.length === 0 ? (
                <Message>Appointment Request Form is Null</Message>
              ) : (
                <ListGroup variant='flush'>
                  {order.orderItems.map((item, index) => (
                    <ListGroup.Item key={index}>
                      <Row>
                        <Col md={1}>
                          <Image
                            src={item.staff.image}
                            alt={item.staff.name}
                            fluid
                            rounded
                          />
                        </Col>
                        <Col>
                          <Link to={`/staffs/${item.staff}`}>
                            {item.staff.name}
                          </Link>
                        </Col>
                        <Col md={3}>
                          <Row>{item.staff.department}</Row>
                          <Row>{ }</Row>
                          <Row>{item.staff.position}</Row>
                        </Col>
                        <Col>
                          <Message {...getStatusMessage(item.status)}>
                            {getStatusMessage(item.status).message}
                          </Message>
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </ListGroup.Item>
          </ListGroup>
        </Col>


      </Row>
    </>
  ) : (
    <Message>Order is not found.</Message>
  )
}

export default OrderScreen
