import React, { useEffect, useState } from 'react'
import { Link, useNavigate, useLocation, useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, ListGroup, Image, Form, Button, Card } from 'react-bootstrap'
import { addToPreAppointment } from '../actions/preAppointmentAction'

const PreAppointmentScren = () => {
  const { id: staffId } = useParams() // 获取路由参数中的 id
  const location = useLocation() // 获取当前的 location 对象
  const [appointmentDate, setAppointmentDate] = useState('')
  const [appointmentSlot, setAppointmentSlot] = useState('')
  const [illnessDescription, setDescription] = useState('')

  // 处理文本输入框变化的函数
  const handleDescriptionChange = (e) => {
    setDescription(e.target.value)
  }

  // 提交预约的处理函数
  const checkoutHandler = () => {
    // 这里可以使用description状态发送数据到服务器
    console.log('Submitting:', illnessDescription)
    // 实际应用中，这里可能是一个调用API的函数
  }


  useEffect(() => {
    // 解析查询字符串参数
    const queryParams = new URLSearchParams(location.search)
    const date = queryParams.get('date')
    const slot = queryParams.get('slot')

    // 如果URL中包含日期和时间段，则设置状态
    if (date && slot) {
      setAppointmentDate(date)
      setAppointmentSlot(slot)
    }

    // 此处可能还有其他需要在组件加载时执行的逻辑
  }, [location.search]) // 依赖项是 location.search，这样任何查询字符串的变化都会触发这个 effect


  useEffect(() => {
    if (staffId) {
      dispatch(addToPreAppointment(staffId, date, slot))
    }
  }, [dispatch, staffId, date, slot])


  // ... 使用 appointmentDate 和 appointmentSlot 来渲染组件


  const dispatch = useDispatch()
  const preAppointment = useSelector((state) => state.preAppointment)
  const { preAppointmentItems } = preAppointment || { preAppointmentItems: [] }

  return (

    <Row>
      <Col md={8}>
        <h1>Pre-Appointment-list</h1>
        {preAppointmentItems.length === 0 ? (
          <Message>
            preAppointMent list为空<Link to='/'>返回主页</Link>
          </Message>
        ) : (
          <ListGroup variant='flush'>
            {preAppointmentItems.map((item) => (
              <ListGroup.Item key={item.staff}>
                <Row>
                  <Col md={2}>
                    <Image src={item.image} alt={item.name} fluid rounded />
                  </Col>
                  <Col md={3}>
                    <Link to={`/staffs/${item.staff}`}>{item.name}</Link>
                  </Col>
                  <Col md={2}>{item.position}</Col>
                  <Col md={2}>{appointmentDate}
                  </Col>
                  <Col md={2}>{appointmentSlot}
                  </Col>

                  <Col>
                    <Button
                      type='button'
                      onClick={() => removerFromPreAppHandler(item.staff)}
                    >
                      <i className='fas fa-trash'></i>
                    </Button>
                  </Col>
                </Row>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </Col>
      <Col md={4}>
        <Card>
          <ListGroup variant='flush'>
            <ListGroup.Item>
              <Form.Group controlId="description">
                <Form.Label>病情描述</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  value={description}
                  onChange={handleDescriptionChange}
                  placeholder="请详细描述您的病情"
                />
              </Form.Group>
            </ListGroup.Item>
            <ListGroup.Item>
              <Button
                type='button'
                className='btn-block'
                disabled={description.trim().length === 0}
                onClick={checkoutHandler}
              >
                去预约
              </Button>
            </ListGroup.Item>
          </ListGroup>
        </Card>
      </Col>
    </Row>



  )
}

export default PreAppointmentScren
