import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { ListGroup, Row, Col, Image, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { useParams } from 'react-router-dom'
import { getOrderDetails } from '../actions/orderAction'
import Message from '../components/Message'
import Loader from '../components/Loader'

const OrderScreen = () => {
  // 使用 useParams 钩子获取路由参数
  const { id: orderId } = useParams()
  const dispatch = useDispatch()

  const orderDetails = useSelector((state) => state.orderDetails)
  const { order, loading, error } = orderDetails


  useEffect(() => {
    // 确保 order 已经定义并且 order._id 不等于 orderId
    if (!order || (order && order._id !== orderId)) {
      dispatch(getOrderDetails(orderId))
    }
    // eslint-disable-next-line
  }, [order, orderId])


  const getStatusMessage = (status) => {
    switch (status) {
      case 'Scheduled':
        return { variant: 'info', message: '预定' }
      case 'Completed':
        return { variant: 'success', message: '已完成' }
      case 'Cancelled':
        return { variant: 'danger', message: '已取消' }
      case 'No Show':
        return { variant: 'warning', message: '未出现' }
      default:
        return { variant: 'secondary', message: '状态未知' }
    }
  }


  return loading ? (
    <Loader />
  ) : error ? (
    <Message variant='danger'>{error}</Message>
  ) : order ? (
    <>
      <h1>Appointment Number：{order._id}</h1>
      <Row>
        <Col md={8}>
          <ListGroup variant='flush'>
            <ListGroup.Item>
              <h2>Description of the illness or complain</h2>
              <p>
                <strong>收件人地址：</strong>
              </p>
              <p>
                <strong>Name:</strong>
                {order.user.name}
              </p>
              <p>
                {' '}
                <strong>Email:</strong>
                <a href={`mailto:${order.user.email}`}>{order.user.email}</a>
              </p>
              <p>
                {order.illnessDescription}
              </p>

            </ListGroup.Item>



            <ListGroup.Item>
              <h2>产品订单</h2>
              {order.orderItems.length === 0 ? (
                <Message>预约为空</Message>
              ) : (
                <ListGroup variant='flush'>
                  {order.orderItems.map((item, index) => (
                    <ListGroup.Item key={index}>
                      <Row>
                        <Col md={1}>
                          <Image
                            src={item.staff.image}
                            alt={item.staff.name}
                            fluid
                            rounded
                          />
                        </Col>
                        <Col>
                          <Link to={`/staffs/${item.staff}`}>
                            {item.staff.name}
                          </Link>
                        </Col>
                        <Col md={3}>
                          <div>医生姓名: {item.staff.name}</div>
                          <div>部门: {item.staff.department}</div>
                          <div>职位: {item.staff.position}</div>
                        </Col>
                        <Col>
                          <Message {...getStatusMessage(item.status)}>
                            {getStatusMessage(item.status).message}
                          </Message>
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </ListGroup.Item>
          </ListGroup>
        </Col>

        <Col md={4}>
          <Card>
            <ListGroup variant='flush'>
              <ListGroup.Item>
                <h2>订单详情</h2>
              </ListGroup.Item>

            </ListGroup>
          </Card>
        </Col>
      </Row>
    </>
  ) : (
    <Message>Order is not found.</Message>
  )
}

export default OrderScreen
