import { createStore, combineReducers, applyMiddleware } from 'redux'
import { compose } from 'redux'
import thunk from 'redux-thunk'
import { composeWithDevTools } from 'redux-devtools-extension'
import {
  productListReducer,
  productDeatilsReducer,
} from './reducers/productReducers'
const reducer = combineReducers({
  productList: productListReducer,
  productDetails: productDeatilsReducer,
})

const initialState = {}

const middleware = [thunk]

const store = createStore(
  reducer,
  initialState,
  composeWithDevTools(applyMiddleware(...middleware))
)

// 如果 Redux DevTools Extension 可用，使用它，否则使用 Redux 的 compose
const composeEnhancers =
  typeof window === 'object' &&
    window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__
    ? window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__({
      // 你可能想要在这里设置选项，如 actionSanitizer, stateSanitizer
      trace: true,
      traceLimit: 25,
    })
    : compose

// 使用 composeEnhancers 调用 applyMiddleware() 和其他 store 增强器
const enhancer = composeEnhancers(
  applyMiddleware(...middleware),
  // 其他 store 增强器如果有的话
)

export default store
